mapper.insert:
INSERT INTO TRIP_MEMBER(
	MEM_ID
	, MEM_PW
	, MEM_NAME
	, MEM_TELL
	, MEM_BIRTHDAY
	, MEM_GENDER
	, MEM_ZIPCODE
	, MEM_ADDR
	, MEM_ADDR_DETAIL
) VALUES(
	#{memId}
	, #{memPw}
	, #{memName}
	, #{memTell}
	, #{memBirthday}
	, #{memGender}
	, #{memZipcode}
	, #{memAddr}
	, #{memAddrDetail}
)

mapper.select:
SELECT MEM_ID
FROM TRIP_MEMBER
WHERE MEM_ID = #{memId}

SELECT MEM_TELL
FROM TRIP_MEMBER
WHERE MEM_TELL = #{memTell}

SELECT MEM_ID
	, MEM_NAME
FROM TRIP_MEMBER
WHERE MEM_TELL = #{memTell}
AND MEM_NAME = #{memName}
AND MEM_GENDER = #{memGender}
AND MEM_BIRTHDAY = #{memBirthday}

SELECT TEMP_NUM
	, TEMP_NAME
FROM TEMP_MEMBER
WHERE TEMP_NUM = #{tempNum}
AND TEMP_NAME = #{tempName}

mapper.update:
UPDATE TRIP_MEMBER
SET MEM_PW = #{memPw}
WHERE MEM_ID = #{memId}
AND IS_DEACTIVATE = 'N'

UPDATE TRIP_MEMBER
SET MEM_PW = #{memPw}
	, MEM_NAME = #{memName}
	, MEM_GENDER = #{memGender}
	, MEM_BIRTHDAY = #{memBirthday}
	, MEM_ZIPCODE = #{memZipcode}
	, MEM_ADDR = #{memAddr}
	, MEM_ADDR_DETAIL = #{memAddrDetail}
	, MEM_TELL = #{memTell}
WHERE MEM_ID = #{memId}
AND IS_DEACTIVATE = 'N'

UPDATE TRIP_MEMBER
SET IS_DEACTIVATE = 'Y'
WHERE MEM_ID = #{memId}

mapper.insert:
INSERT INTO TEMP_MEMBER (
	TEMP_NUM
	, TEMP_NAME
	, TEMP_TELL
	, TEMP_BIRTHDAY
	, TEMP_EMAIL
	, TEMP_ADDR
	, TEMP_ADDR_DETAIL
	, TEMP_ZIPCODE
	, TEMP_GENDER
	, BOOK_NUM
) VALUES (
	(SELECT NVL(MAX(TEMP_NUM), 0) + 1 FROM TEMP_MEMBER)	
	, #{tempName}
	, #{tempTell}
	, #{tempBirthday}
	, #{tempEmail}
	, #{tempAddr}
	, #{tempAddrDetail}
	, #{tempZipcode}
	, #{tempGender}
	, #{bookNum}
)

mapper.select:
SELECT COUNT(TEMP_NUM)
FROM TEMP_MEMBER
WHERE TEMP_EMAIL = #{tempEmail}
